document.addEventListener("DOMContentLoaded", function() {
  // Do not reanme the 3 flickity vars here, or the flickty carousel will break.
  var elem = document.querySelector(".carousel");
  var flkty = new Flickity(elem, {
    // options
    cellAlign: "left",
    contain: true,
    freeScroll: false,
    wrapAround: true
  });
  // element argument can be a selector string
  //   for an individual element
  var flkty = new Flickity(".carousel", {
    // options
  });

  // Identify the submitButton, and setup a listener
  document
    .getElementById("submitButton")
    .addEventListener("click", function(event) {
      event.preventDefault();

      // extract the entered email address
      let enteredEmail = document.getElementById("emInput");
      // console.log(enteredEmail);
      if (validateEmail(enteredEmail.value)) {
        alert("Thank you for subscribing.");
        enteredEmail.value = "";
      } else {
        beep();
        alert("Invalid emal address. Please try again.");
        // do not clear the email address here, so that the user can edit it, and re-submit.
      }
    });

  // Identify which item from all unordered lists was clicked, and react accordingly
  const listItems = document.querySelectorAll("ul li"); // return an array for each li in the DOM
  listItems.forEach(function(item) {
    item.onclick = function(e) {
      // console.log(this.innerText); // display the clicked li value
      if (this.innerText === "About") {
        const elmnt = document.getElementById("jumpAbout");
        elmnt.scrollIntoView();
      } else if (this.innerText === "Shop") {
        const elmnt = document.getElementById("jumpShopGrid");
        elmnt.scrollIntoView();
      } else if (this.innerText === "Featured") {
        const elmnt = document.getElementById("jumpFeatured");
        elmnt.scrollIntoView();
      } else if (this.innerText === "Updates") {
        const elmnt = document.getElementById("jumpUpdates");
        elmnt.scrollIntoView();
      }
    };
  });

  // Utility functions
  function validateEmail(email) {
    var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  }

  function beep() {
    new Audio(
      "data:audio/wav;base64,    "
    ).play();
  }
});
